export type RequestStatusType = 'idle' | 'loading' | 'succeeded' | 'failed'

export type NullableRequestErrorType<T> = null | T

const initialState = {
    status: 'idle' as RequestStatusType,
    error:  null as NullableRequestErrorType<string>
}

type InitialStateType = typeof initialState

export type setAppStatusActionType = ReturnType<typeof setAppStatusAC>;
export type setAppErrorActionType = ReturnType<typeof setAppErrorAC>;


export const appReducer = (state: InitialStateType = initialState, action: AppActionsType): InitialStateType => {
    switch (action.type) {
        case 'APP/SET-STATUS':
            return {...state, status: action.status}
        case 'APP/SET-ERROR':
            return {...state, error: action.error}

        default:
            return state
    }
}

export type AppActionsType = setAppStatusActionType | setAppErrorActionType


export const setAppStatusAC  = (status: RequestStatusType) => ({type: 'APP/SET-STATUS', status} as const)

export const setAppErrorAC = (error: string) => ({type: 'APP/SET-ERROR', error} as const)
